  <script>
      $(document).ready(function(){
        var toastmsg = '<?php echo $toastmsg; ?>'
        var toasttype = '<?php echo $toasttype; ?>'
        toastr.options = {
        "closeButton": true,
        "debug": false,
        "progressBar": true,
        "preventDuplicates": false,
        "positionClass": "toast-top-center",
        "onclick": null,
        "showDuration": "400",
        "hideDuration": "1000",
        "timeOut": "7000",
        "extendedTimeOut": "1000",
        "showEasing": "swing",
        "hideEasing": "linear",
        "showMethod": "fadeIn",
        "hideMethod": "fadeOut"
        }

        if(toastmsg!=''){
              if(toasttype == 'error'){
                  toastr.error(toastmsg,'SIRIUS-I');
              }else if(toasttype=='success'){
                  toastr.success(toastmsg,'SIRIUS-I');
              }else if(toasttype=='info'){
                  toastr.info(toastmsg,'SIRIUS-I');
              }
        }

        // $('.i-checks').iCheck({
        //     checkboxClass: 'icheckbox_square-green',
        //     radioClass: 'iradio_square-green',
        // });
        // $('#svpDiv').hide();
        var theForm;
        const forms = document.querySelectorAll(".needs-validation");
        forms.forEach(form => {
          theForm = form;
          form.addEventListener("submit", e => {
              if (!form.checkValidity()) {
                    e.preventDefault()
                    e.stopPropagation()
              }
              form.classList.add("was-validated")
          }, false)
        })

        // Check if passwords match
        const errorMessage = document.getElementById('passwordError');
        $("#password2").on("keyup", function () {
          console.log($("#password2").val());
          // errorMessage.removeClass('invalid-feedback');
          if ($("#password1").val() == $("#password2").val()) {
            console.log('matched');
            errorMessage.textContent = 'Password matched';
            errorMessage.classList.remove('invalid-feedback');
            errorMessage.classList.add('is-valid');
          }else{
            // theForm.classList.add("was-validated")
            console.log('not matched');
            errorMessage.textContent = 'Password does not match';
            errorMessage.classList.remove('is-valid');
            errorMessage.classList.add('is-invalid');
          }
        });

      });


      function validatePassword(password) {
            const strongPasswordRegex = 
                  /^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)(?=.*[@$!%*?&-])[A-Za-z\d@$!%*?&-]{8,}$/;
            const errorMessage = document.getElementById('errorMessage');

            // Check each condition and update the corresponding label
            document.getElementById('minLength').innerHTML = 
                  password.length >= 8 ?
                '<i class="fas fa-check text-success"></i> Minimum 8 characters' :
                '<i class="fas fa-times text-danger"></i> Minimum 8 characters';
            document.getElementById('uppercase').innerHTML = 
                  /[A-Z]/.test(password) ?
                '<i class="fas fa-check text-success"></i> At least one uppercase letter' :
                '<i class="fas fa-times text-danger"></i> At least one uppercase letter';
            document.getElementById('lowercase').innerHTML = 
                  /[a-z]/.test(password) ?
                '<i class="fas fa-check text-success"></i> At least one lowercase letter' :
                '<i class="fas fa-times text-danger"></i> At least one lowercase letter';
            document.getElementById('symbol').innerHTML = 
                  /[@$!%*?&-]/.test(password) ?
                '<i class="fas fa-check text-success"></i> At least one symbol (@$!%*?&-)' :
                '<i class="fas fa-times text-danger"></i> At least one symbol (@$!%*?&-)';

            // Check overall validity and update the error message
            if (strongPasswordRegex.test(password)) {
                errorMessage.textContent = 'Strong Password';
                errorMessage.classList.remove('text-danger');
                errorMessage.classList.add('text-success');
            } else {
                errorMessage.textContent = 'Weak Password';
                errorMessage.classList.remove('text-success');
                errorMessage.classList.add('text-danger');
            }
        }
    </script>

